        '玄武' => '黑道',
        '勾陈' => '黑道'
      }.freeze

      TIAN_SHEN_TYPE_LUCK = {
        '黄道' => '吉',
        '黑道' => '凶'
      }.freeze

      private_constant :YI_JI, :DAY_YI_JI, :TIME_YI_JI, :SHEN_SHA, :DAY_SHEN_SHA

      def self.getTimeZhiIndex(hm)
        # 获取HH:mm时刻的地支序号，非法的时刻返回0
        # :param hm: HH:mm时刻
        # :return: 地支序号，0到11
        return 0 if hm.nil?
        hm = hm[0, 5] if hm.length > 5
        x = 1
        i = 1
        while i <= 21
          hour_start = "#{i < 10 ? '0' : ''}#{i}:00"
          hour_end = "#{i + 1 < 10 ? '0' : ''}#{i + 1}:59"
          return x if hm >= hour_start && hm <= hour_end
          x += 1
          i += 2
        end
        0
      end

      def self.convertTime(hm)
        # 将HH:mm时刻转换为时辰（地支），非法的时刻返回子
        # :param hm: HH:mm时刻
        # :return: 时辰(地支)，如子
        ZHI[getTimeZhiIndex(hm) + 1]
      end

      def self._hex(n)
        # 数字转十六进制
        # :param n: 数字
        # :return: 十六进制
        s = n.to_s(16).upcase
        s = "0#{s}" if s.length < 2
        s
      end
      private_class_method :_hex

      def self.getJiaZiIndex(gan_zhi)
        # 获取干支对应的甲子序号
        # :param gan_zhi: 干支
        # :return: 甲子序号
        JIA_ZI.each_with_index do |jz, i|
          return i if jz == gan_zhi
        end
        -1
      end

      def self.find(name, names, offset)
        names.each_with_index do |n, i|
          return i + offset if n == name
        end
        -1
      end

      def self.getXunIndex(gan_zhi)
        # 获取干支所在旬下标，0-5
        # :param gan_zhi: 干支
        # :return: 旬下标，0-5
        gan = gan_zhi[0]
        zhi = gan_zhi[1]
        gan_index = 0
        zhi_index = 0
        GAN.each_with_index do |g, i|
          if g == gan
            gan_index = i
            break
          end
        end
        ZHI.each_with_index do |z, i|
          if z == zhi
            zhi_index = i
            break
          end
        end
        diff = gan_index - zhi_index
        diff += 12 if diff < 0
        (diff / 2).to_i
      end

      def self.getXun(gan_zhi)
        # 获取干支所在旬
        # :param gan_zhi: 干支
        # :return: 旬
        XUN[getXunIndex(gan_zhi)]
      end

      def self.getXunKong(gan_zhi)
        # 获取干支所在旬对应的旬空(空亡)
        # :param gan_zhi: 干支
        # :return: 旬空(空亡)
        XUN_KONG[getXunIndex(gan_zhi)]
      end

      def self.getDayYi(month_gan_zhi, day_gan_zhi)
        # 获取日宜
        # :param month_gan_zhi: 月干支
        # :param day_gan_zhi: 日干支
        # :return: 宜
        arr = []
        day = _hex(getJiaZiIndex(day_gan_zhi))
        month = _hex(getJiaZiIndex(month_gan_zhi))
        right = DAY_YI_JI
        index = right.index("#{day}=")
        while index
          right = right[(index + 3)..]
          left = right
          if left.include?("=")
            left = left[0, right.index("=") - 2]
          end
          matched = false
          months = left[0, left.index(":")]
          (0...months.length).step(2) do |i|
            if months[i, 2] == month
              matched = true
              break
            end
          end
          if matched
            ys = left[(left.index(":") + 1), left.index(",")]
            (0...ys.length).step(2) do |i|
              arr << YI_JI[ys[i, 2].to_i(16)]
            end
            break
          end
          index = right.index("#{day}=")
        end
        arr << "无" if arr.empty?
        arr
      end

