      def self.getDayJiShen(lunar_month, day_gan_zhi)
        # 获取日吉神
        # :param lunar_month: 月
        # :param day_gan_zhi: 日干支
        # :return: 日吉神
        arr = []
        day = _hex(getJiaZiIndex(day_gan_zhi))
        month = lunar_month.abs.to_s(16).upcase
        index = DAY_SHEN_SHA.index("#{month}#{day}=")
        if index
          left = DAY_SHEN_SHA[(index + 4)..]
          if left.include?("=")
            left = left[0, left.index("=") - 3]
          end
          js = left[0, left.index(",")]
          (0...js.length).step(2) do |i|
            arr << SHEN_SHA[js[i, 2].to_i(16)]
